// server.js
const express = require('express');
const app = express();
const port = 5000;

// CORS enable karna (agar alag origin se request aa rahi ho)
const cors = require('cors');
app.use(cors());

// API route
app.get('/get-text', (req, res) => {
  res.json({ text: "Yeh text backend se aaya hai!" });
});

app.listen(port, () => {
  console.log(`Server running on http://localhost:${port}`);
});
